var str1 = '';
var str2 = '99958321098765432102222223333211112222222222222222222';

function addTwoNum(str1, str2){
  var carry = 0,  //进位值   0 不进位，  1进位
    l1 = str1.length, //长度 
    l2 = str2.length, //长度
    arr = []; // 

    var max = Math.max(l1, l2); //最大长度的值
    console.log(l1);
    for (var i = l1-1, j = l2-1, n = max - 1; n >= 0; n--, i--, j--) {
      var sum = (+str1[i] || 0) + (+str2[j] || 0) + carry;
      if (sum >= 10) {
        carry = 1;
        arr.push(sum-10);
      } else {
        carry = 0;
        arr.push(sum);
      }
    }
    if (carry > 0) arr.push(carry);
    
    return arr.reverse().join('');
}

console.log(addTwoNum(str1, str2));